mapper.insert:

insert into detection_account (id, created_at, updated_at, 
  cost_record_id, account_name, account_score, 
  detail_score0, detail_score1, detail_score2, 
  detail_score3, detail_score4, user_info_id,cost
  )
values (#{id,jdbcType=VARCHAR}, #{created_at,jdbcType=TIMESTAMP}, #{updated_at,jdbcType=TIMESTAMP}, 
  #{cost_record_id,jdbcType=VARCHAR}, #{account_name,jdbcType=VARCHAR}, #{account_score,jdbcType=REAL}, 
  #{detail_score0,jdbcType=REAL}, #{detail_score1,jdbcType=REAL}, #{detail_score2,jdbcType=REAL}, 
  #{detail_score3,jdbcType=REAL}, #{detail_score4,jdbcType=REAL}, #{user_info_id,jdbcType=VARCHAR}, #{cost,jdbcType=REAL}
  )
