mapper.select:

select 
IFNULL(count(distinct(account_name)),0)
from detection_account
where 1=1

	and created_at  >=   #{range_date_start}


	and created_at  <=   #{range_date_end}


select 

from detection_account
where user_info_id = #{user_info_id}
and cost_record_id = #{recordId}

    and account_name like "%"#{account_name}"%"


    and account_score  >=  #{account_score_start}


    and account_score  <=  #{account_score_end}

order by created_at desc
limit ${(pageNo-1)*pageSize},${pageSize}


select 
IFNULL(count(id),0)
from detection_account
where user_info_id = #{user_info_id}
and cost_record_id = #{recordId}

    and account_name like "%"#{account_name}"%"


    and account_score  >=  #{account_score_start}


    and account_score  <=  #{account_score_end}


select 

from detection_account
where user_info_id = #{user_info_id}
order by account_score desc
limit ${topNum}


select 
*
from detection_account
where cost_record_id = #{recordId}
order by created_at desc

    limit ${(pageNo-1)*pageSize},${pageSize}


select 
*
from detection_account
where cost_record_id = #{recordId}
