mapper.select:

select 

from detection_account
where user_info_id = #{user_info_id}
and cost_record_id = #{recordId}
limit ${(pageNo-1)*pageSize},${pageNo*pageSize}


select 

from detection_account
where user_info_id = #{user_info_id}

    and account_name = #{userGetScanHistoryParameters.scan_account}



    	and account_score  >=  80


    	and account_score  <  80


    	and account_name = -1


    	and account_name = -2



    and created_at  >=  #{userGetScanHistoryParameters.scan_date_start}


    and created_at  <=  #{userGetScanHistoryParameters.scan_date_end}


    limit ${(userGetScanHistoryParameters.current_page-1)*pageSize},${userGetScanHistoryParameters.current_page*pageSize}

mapper.insert:

insert into detection_account (id, created_at, updated_at, 
  cost_record_id, account_name, account_score, 
  detail_score0, detail_score1, detail_score2, 
  detail_score3, detail_score4, user_info_id
  )
values (#{id,jdbcType=VARCHAR}, #{created_at,jdbcType=DATE}, #{updated_at,jdbcType=DATE}, 
  #{cost_record_id,jdbcType=VARCHAR}, #{account_name,jdbcType=VARCHAR}, #{account_score,jdbcType=REAL}, 
  #{detail_score0,jdbcType=REAL}, #{detail_score1,jdbcType=REAL}, #{detail_score2,jdbcType=REAL}, 
  #{detail_score3,jdbcType=REAL}, #{detail_score4,jdbcType=REAL}, #{user_info_id,jdbcType=VARCHAR}
  )
