mapper.select:

select 
IFNULL(count(id),0)
from query_recharge
where 1 = 1

    and user_info_id = #{queryRechargeListParameters.user_id}


    and phone_no = #{queryRechargeListParameters.user_phone}


    and created_at  >=  #{queryRechargeListParameters.created_start_date}


    and created_at  <=  #{queryRechargeListParameters.created_end_date}


    and recharge_type = #{queryRechargeListParameters.recharge_type}


    and recharge_status = #{queryRechargeListParameters.recharge_status}


    and rmb_amount  >=  #{queryRechargeListParameters.rmb_amount_start}


    and rmb_amount  <=  #{queryRechargeListParameters.rmb_amount_end}


    and id = #{queryRechargeListParameters.recharge_id}


select 
*
from query_recharge
where 1 = 1

    and user_info_id = #{queryRechargeListParameters.user_id}


    and phone_no = #{queryRechargeListParameters.user_phone}


    and created_at  >=  #{queryRechargeListParameters.created_start_date}


    and created_at  <=  #{queryRechargeListParameters.created_end_date}


    and recharge_type = #{queryRechargeListParameters.recharge_type}


    and recharge_status = #{queryRechargeListParameters.recharge_status}


    and rmb_amount  >=  #{queryRechargeListParameters.rmb_amount_start}


    and rmb_amount  <=  #{queryRechargeListParameters.rmb_amount_end}


    and id = #{queryRechargeListParameters.recharge_id}

order by created_at desc

    limit ${(queryRechargeListParameters.pageNo-1)*pageSize},${pageSize}


select 
*
from query_recharge
where 1 = 1

    and user_info_id = #{queryRechargeListParameters.user_id}


    and phone_no = #{queryRechargeListParameters.user_phone}


    and created_at  >=  #{queryRechargeListParameters.created_start_date}


    and created_at  <=  #{queryRechargeListParameters.created_end_date}


    and recharge_type = #{queryRechargeListParameters.recharge_type}


    and recharge_status = #{queryRechargeListParameters.recharge_status}


    and rmb_amount  >=  #{queryRechargeListParameters.rmb_amount_start}


    and rmb_amount  <=  #{queryRechargeListParameters.rmb_amount_end}


    and id = #{queryRechargeListParameters.recharge_id}

order by created_at desc
